import React from "react";
import axios from 'axios';

export default function Send(props){
    var {cartItems} =props;
    var carrello =[];
    {cartItems.map((item) => (
      carrello += "1"+ item.nome_prod+"-"+item.qty
      ))
    }
    axios.post('http://127.0.0.1:8000/ordinazioni/', {
        data: "2022-03-24T09:45:56Z",
        nome_prenot: "dafile",
        carrello: [
            "gelato x 1",
            "caffe 1",
            "asdad 2",
            "sdada 3",
        ]
    })
      .then(function (response) {
          res=response;
        console.log(response);
      })
      return(res);
}
