import React from 'react';
import Send from './Send';

export default function Basket(props) {
  const { cartItems, onAdd, onRemove } = props;
  const taxPrice = cartItems.prezzo * 0.14;
  const nome_prenot = localStorage.getItem('nome');
  const totalPrice = cartItems.reduce((a,c)=> a + c.prezzo *c.qty,0);
  return (
    <aside className="block col-1">
      <h2>Ordinazione</h2>
      <input type="text" hidden></input>
      <div>
        {cartItems.length === 0 && <div>Nessun prodotto</div>}
        {cartItems.map((item) => (
          <div key={item.id} className="row">
            <div className="col-2"><p>{item.nome_prod}x{item.qty} -  tot <span class="tot" value={item.prezzo * item.qty}>{item.prezzo * item.qty}</span>€</p></div>
           
            <div className="col-2">
              <button onClick={() => onRemove(item)} className="remove">
                -
              </button>{' '}
              <button onClick={() => onAdd(item)} className="add">
                +
              </button>
            </div>

            <div className="col-2 text-right">
            
            </div>
          </div>
        ))}
        
        {cartItems.length !== 0 && (
          <>
            <hr></hr>
            <div className="row">
              <div className="col-2">Prezzo</div>
              <div className="col-1 text-right">€{cartItems.prezzo}</div>
            </div>            
            

            <div className="row">
              <div className="col-2">
                <strong>Total Price</strong>
              </div>
              <div className="col-1 text-right">
              
                <strong>€{totalPrice}</strong>
              </div>
            </div>
            <hr />
            <div className="row">
              <button onClick={Send()}>
                Checkout
              </button>
            </div>
          </>
        )}
      </div>
    </aside>
  );
}
