      articoli[i]= item.nome_prod+"-"+item.qty
     ));
    function invia() {
    var res;    
    axios.post('http://127.0.0.1:8000/ordinazioni/', {        
        nome_prenot: localStorage.getItem('nome'),
        carrello: articoli,
        totale:totalPrice,
    })
      .then(function (response) {
          res=response;
