import React from "react";
import axios from 'axios';

export default function Send(cartItem){
    var res;
    JSON.stringify([cartItem])
    axios.post('http://127.0.0.1:8000/ordinazioni/', {
        data: "2022-03-24T09:45:56Z",
        nome_prenot: "dafile",
        carrello: [{"id":1,"nome_prod":"Caffè","description":"tazzuriell e cafè","prezzo":"1.20","note":"ZUCCHERO GRATIS","categoria":"BAR","qty":4},{"id":4,"nome_prod":"ghiacciolo","description":"freddo","prezzo":"99.00","note":"sd","categoria":"Bar","qty":5},{"id":3,"nome_prod":"Cornetto","description":"creme varie","prezzo":"2.00","note":"scrivere la crema sulle note","categoria":"BAR","qty":2}],
        
    })
      .then(function (response) {
          res=response;
        console.log(response);
      })
      return(res);
}
