import React from "react";
import axios from 'axios';

export default function Send(cartItem){
    var res;
    var ordine = JSON.stringify([cartItem])
    axios.post('http://127.0.0.1:8000/ordinazioni/', {
        data: "2022-03-24T09:45:56Z",
        nome_prenot: "dafile",
        carrello: [ordine
        ]
    })
      .then(function (response) {
          res=response;
        console.log(response);
      })
      return(res);
}
