import React from "react";
import axios from 'axios';

export default function Send(cartItem,nome){
    var res;
    axios.post('http://127.0.0.1:8000/ordinazioni/', {
        
        nome_prenot:nome ,
        carrello: [cartItem
        ]
    })
      .then(function (response) {
          res=response;
        console.log(response);
      })
      return(res);
}
