import React from 'react';

import Badge from '@mui/material/Badge';
import Stack from '@mui/material/Stack';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import Fab from '@mui/material/Fab';
import Divider from '@mui/material/Divider';
import Basket from './Basket';
import LocalGroceryStoreSharpIcon from '@mui/icons-material/LocalGroceryStoreSharp';


export default function Header(props) {
  const [state, setState] = React.useState({       
    right: false,
  });
  const { cartItems, onAdd, onRemove, countCartItems} = props
  const toggleDrawer = (anchor, open) => (event) => {
    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }

    setState({ ...state, [anchor]: open });
  };

  const list = (anchor) => (
    <Box
      sx={{ width: anchor === 'top' || anchor === 'bottom' ? 'auto' : 50 }}
      role="presentation"
      onClick={toggleDrawer(anchor, false)}
      onKeyDown={toggleDrawer(anchor, false)}
    >
      <Basket
          cartItems={cartItems}
          countCartItems={cartItems.length}
          onAdd={onAdd}
          onRemove={onRemove}
        ></Basket>
      <Divider />
      
    </Box>
  );
  const fabStyle = {
    position: 'absolute',
    bottom: 16,
    right: 16,
  };
  return (
    <div>
      {['right'].map((anchor) => (
        <React.Fragment key={anchor}>
          
      <Box sx={{ '& > :not(style)': { m: 1 } }}>
            <Fab size="large" color="primary" aria-label="add" onClick={toggleDrawer(anchor, true)} sx={fabStyle}>
            <Stack spacing={2} direction="row">      
              <Badge badgeContent= {props.countCartItems ? (
                    <span>{props.countCartItems}</span>
                  ) : (
                    '0'
                  )} color="secondary">
                <LocalGroceryStoreSharpIcon color="white" />
              </Badge>
              </Stack>
              </Fab>
          </Box>    
                <Drawer
                  anchor={anchor}
                  open={state[anchor]}
                  onClose={toggleDrawer(anchor, false)}
                >
                  {list(anchor)}
                </Drawer>
              </React.Fragment>
            ))}
        </div>
      );
    
}
