import React from 'react';
import Badge from '@mui/material/Badge';
import Stack from '@mui/material/Stack';
import Box from '@mui/material/Box';
import Drawer from '@mui/material/Drawer';
import Fab from '@mui/material/Fab';
import Divider from '@mui/material/Divider';
import Basket from './Basket';
import LocalGroceryStoreSharpIcon from '@mui/icons-material/LocalGroceryStoreSharp';


export default function Header(props) {
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const list = (anchor) => (
    <Box
      sx={{ width: anchor === 'top' || anchor === 'bottom' ? 'auto' : 400 }}
      role="presentation"
      onClick={toggleDrawer(anchor, false)}
      onKeyDown={toggleDrawer(anchor, false)}
    >
      <Basket
          cartItems={cartItems}
          countCartItems={cartItems.length}
          onAdd={onAdd}
          onRemove={onRemove}
        ></Basket>
      <Divider />
      <button onClick={handleDrawerClose}>z</button>
    </Box>
  );
  const fabStyle = {
    position: 'fixed',
    bottom:30,
    right: 10,
  };
  return (
    <div>
      {['right'].map((anchor) => (
        <React.Fragment key={anchor}>          
      <Box sx={{ '& > :not(style)': { m: 1 } }}>
            <Fab size="large" color="primary" aria-label="add" onClick={toggleDrawer(anchor, true)} sx={fabStyle}>
            <Stack spacing={2} direction="row">      
              <Badge badgeContent= {props.countCartItems ? (
                    <span>{props.countCartItems}</span>
                  ) : (
                    '0'
                  )} color="secondary">
                <LocalGroceryStoreSharpIcon color="white" />
              </Badge>
              </Stack>              
              </Fab>              
        </Box>    
        <Drawer
                  sx={{
                    width: 300,
                    flexShrink: 0,
                    '& .MuiDrawer-paper': {
                      width: 200,
                    },
                  }}
                  variant="persistent"
                  anchor="right"
                  open={open}             

                >
                  {list(anchor)}
                </Drawer>      
        </React.Fragment>
            ))}
        </div>
      );
    
}
