 import axios from "axios";
 import React, { useEffect, useState } from "react";

 function Data()   {
 const [val, setVal] = useState();

 const getAnswer = async () => {
   const { data } = await axios('http://127.0.0.1:8000/prodotti/');
   setVal(data);
 };

 useEffect(() => {
   getAnswer();
 }, []);


  return (val);
}
export default val;
