import React from 'react';

import Badge from '@mui/material/Badge';
import Stack from '@mui/material/Stack';
import Box from '@mui/material/Box';
import SwipeableDrawer from '@mui/material/SwipeableDrawer';
import Fab from '@mui/material/Fab';
import Divider from '@mui/material/Divider';
import Basket from './Basket';
import LocalGroceryStoreSharpIcon from '@mui/icons-material/LocalGroceryStoreSharp';


export default function Header(props) {
  
  const { cartItems, onAdd, onRemove, countCartItems} = props
  const [open, setOpen] = useState<boolean>(false);

  const list = (anchor) => (
    <Box
      sx={{ width: anchor === 'top' || anchor === 'bottom' ? 'auto' : 400 }}
      role="presentation"
      onOpen={toggleDrawer(anchor, false)}
      onClose={toggleDrawer(anchor, false)}
    >
      <Basket
          cartItems={cartItems}
          countCartItems={cartItems.length}
          onAdd={onAdd}
          onRemove={onRemove}
        ></Basket>
      <Divider />
      
    </Box>
  );
  const fabStyle = {
    position: 'absolute',
    bottom: 16,
    right: 16,
  };
  return (
    <div>
      
        <React.Fragment key='right'>
          
      <Box sx={{ '& > :not(style)': { m: 1 } }}>
            <Fab size="large" color="primary" aria-label="add" onClick={SwipeableDrawer('right', true)} sx={fabStyle}>
            <Stack spacing={2} direction="row">      
              <Badge badgeContent= {props.countCartItems ? (
                    <span>{props.countCartItems}</span>
                  ) : (
                    '0'
                  )} color="secondary">
                <LocalGroceryStoreSharpIcon color="white" />
              </Badge>
              </Stack>
              </Fab>
          </Box>    
                <SwipeableDrawer
                   anchor="right"
                   open={open}
                   onClose={() => setOpen(false)}
                   onOpen={() => {}}
                >
                  {list('right')}
                </SwipeableDrawer>
              </React.Fragment>
            )}
        </div>
      );
    
}
